# -*- coding: utf-8 -*-

import tkinter as tk

def demander_texte(question):
    """
    question est une chaine de caractères
    ~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~
    Ouvre une fenêtre avec le message "question" et attend une chaine de caractères.
    """
    def sauver_valeur(event=None):#la fonction sauve la valeur dans value et ferme la fenetre
        value.set(entree.get())
        fenetre.destroy()

    fenetre = tk.Tk()
    fenetre.title(question)

    value = tk.StringVar()

    entree = tk.Entry(fenetre, textvariable=value, width=30)
    entree.pack()
    bouton=tk.Button(fenetre,text='Valider',command=sauver_valeur)
    bouton.pack()

    fenetre.mainloop()
    return value.get()
