import os
import thonny
import sys

if 'HOMESHARE' in os.environ:
    user_dir = os.path.join(os.environ['HOMESHARE'],'python','.thonny')
else:
    user_dir = os.path.join(os.environ['USERPROFILE'],'python','.thonny')


os.environ["THONNY_USER_DIR"] = os.path.abspath(user_dir)
thonny.THONNY_USER_DIR = os.path.abspath(user_dir)